import './App.css';
import { use, useState, useEffect  } from 'react';
import { v4 as uuidv4 } from 'uuid';


function App() { 

  const[newInput, setnewInput]  = useState("");
  const[todoList, setTodoList] = useState([]);
  const[editTaskId, setEditTaskId] = useState(null);


  useEffect(() => { 
    const savedTodos = localStorage.getItem("todoList");

    if (savedTodos) {
      setTodoList(JSON.parse(savedTodos));
    }
  }, []);

  const addTodo = ()=>{ 

    if (!newInput.trim()){
      return
    }

     if (editTaskId){
       todoList.find(task=> task.id===editTaskId).task = newInput;

        localStorage.setItem("todoList", JSON.stringify(todoList));

        setTodoList(todoList);
        setnewInput("");
        setEditTaskId("");

     }else{
      const newValue = {
        id: uuidv4(),
        task: newInput
      }

      const updateList = [...todoList, newValue];

      localStorage.setItem("todoList", JSON.stringify(updateList));

      setTodoList(updateList);
      setnewInput("");
    }    
  }

  function visualizeToUpdate(id){
       const notepadEdit = todoList.find((task)=> task.id === id);

       if (notepadEdit) {
          setnewInput(notepadEdit.task);
          setEditTaskId(id);
       }

  }

  function deleteTodo(id){
    const updateList = todoList.filter((task)=> task.id !== id);

    localStorage.setItem("todoList", JSON.stringify(updateList));

    setTodoList(updateList);
  }


  function currentTodo(e){
    setnewInput(e.target.value);
  }


  return (
    <>
      {/* <div className='flex items-center justify-center '> */}
      <div className='grid place-items-center'>
        <div className='border-3 border-black'>
        {/* <div className="border-4 border-black"> */}
              Google Keep
          </div>

          <div>
              <input onChange={currentTodo} value={newInput} class='border-2 border-black pe-100'></input> 
              <button onClick={addTodo}>
                  Save          
                </button>
          </div>

          <div>
              <ul>
                { todoList.map((task) => (
                        <li key={task.id}>
                              {task.task}
                            <button onClick={()=>visualizeToUpdate(task.id)}>Update</button>
                            <button onClick={()=> deleteTodo(task.id)}>Delete</button>
                        </li>

                    ))}
              </ul>
          </div>
      </div>
    </>

  );
}

export default App;
