import logo from './logo.svg';
import './App.css';

function handleAddTodo(){
  const newTodo =  {
      id: 1,
      task: 'my third task'
  }

  return (
    <li>{newTodo.task}</li>
  )
};

function App() {
  return (
    <>
      <div>
          Google Keep
      </div>
      <div>
          <input></input>
          <button>
            Add input
          </button>
      </div>

      <div>
          <textarea></textarea>
          <button>
            Add textarea
          </button>
      </div>

      <ul>
        <li>
          {handleAddTodo}
        </li>
        <li>
            This this second task
        </li>
      </ul>

    </>

  );
}

export default App;
