import './App.css';
import { use, useState, useEffect  } from 'react';
import { v4 as uuidv4 } from 'uuid';


function App() { 

  const[newInput, setnewInput]  = useState("");
  const[todoList, setTodoList] = useState([]);
  const[editTaskId, setEditTaskId] = useState(null);


  useEffect(() => { 
    const savedTodos = localStorage.getItem("todoList");

    if (savedTodos) {
      setTodoList(JSON.parse(savedTodos));
    }
  }, []);

  const addTodo = ()=>{ 

    if (!newInput.trim()){
      return
    }

     if (editTaskId){
        todoList.find(task=> task.id===editTaskId).task = newInput;
     }else{
      const newValue = {
        id: uuidv4(),
        task: newInput
      }

      const updateList = [...todoList, newValue];

      localStorage.setItem("todoList", JSON.stringify(updateList));

      setTodoList(updateList);
      setnewInput("");
    }    
  }

  function visualizeToUpdate(id){
       const notepadEdit = todoList.find((task)=> task.id === id);

       if (notepadEdit) {
          setnewInput(notepadEdit.task);
          setEditTaskId(id);
       }

  }

  function currentTodo(e){
    setnewInput(e.target.value);
  }


  return (
    <>
      <div>
          Google Keep
      </div>

      <div>
          <input className = "notepad" onChange={currentTodo} value={newInput}></input> 
          <button onClick={addTodo}>
              Save          
            </button>
      </div>

      <div>
          <ul>
            { todoList.map((task) => (
                    <li key={task.id}>
                           {task.task}
                        <button onClick={()=>visualizeToUpdate(task.id)}>Update</button>
                        <button>Delete</button>
                    </li>

                ))}
          </ul>
      </div>
    </>

  );
}

export default App;
